import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { motion } from 'framer-motion';
import { toast } from 'react-hot-toast';
import CodeEditor from '../components/Editor/CodeEditor';
import { problemsAPI, submissionsAPI } from '../services/api';
import { codeTemplates, programmingLanguages } from '../data/mockData';
import { Tag } from 'lucide-react';
import { useAuth } from '../contexts/AuthContext';

const ProblemDetail = () => {
  const { slug } = useParams();
  const { user, isAuthenticated } = useAuth();

  const [problem, setProblem] = useState(null);
  const [loading, setLoading] = useState(true);
  const [language, setLanguage] = useState('javascript');
  const [initialCode, setInitialCode] = useState('');

  useEffect(() => {
    const fetchProblem = async () => {
      try {
        setLoading(true);
        const res = await problemsAPI.getBySlug(slug);
        const p = res.data.problem;
        setProblem(p);
        setInitialCode(codeTemplates[language] || '');
      } catch (err) {
        console.error(err);
        toast.error('Error loading problem');
      } finally {
        setLoading(false);
      }
    };
    if (slug) fetchProblem();
  }, [slug, language]);

  if (loading) return <div>Loading...</div>;
  if (!problem) return <div>Problem not found</div>;

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 p-8">
      <div className="max-w-4xl mx-auto bg-white dark:bg-gray-800 rounded-lg shadow p-6">
        <motion.div>
          <h1 className="text-2xl font-bold text-gray-900 dark:text-white">{problem.title}</h1>
          <div className="mt-2">
            <Tag className="inline-block mr-1" /> Difficulty: {problem.difficulty}
          </div>
        </motion.div>

        <motion.div className="prose prose-lg dark:prose-invert mt-6">
          <div dangerouslySetInnerHTML={{ __html: problem.description }} />
        </motion.div>

        {problem.testCases?.some(tc => tc.is_sample) && (
          <motion.div className="mt-8">
            <h3 className="text-lg font-semibold text-gray-900 dark:text-white">Sample Test Cases</h3>
            {problem.testCases.filter(tc => tc.is_sample).map((tc, i) => (
              <div key={i} className="bg-gray-100 dark:bg-gray-700 p-4 rounded my-2">
                <strong>Input:</strong> <pre className="whitespace-pre-wrap">{tc.input}</pre>
                <strong>Expected Output:</strong> <pre className="whitespace-pre-wrap">{tc.expected_output}</pre>
              </div>
            ))}
          </motion.div>
        )}

        <div className="mt-10">
          <CodeEditor
            onRunCode={() => {}}
            onSubmitCode={() => {}}
            isRunning={false}
            isSubmitting={false}
            initialCode={initialCode}
            language={language}
            setLanguage={setLanguage}
            submissionResult={null}
          />
        </div>
      </div>
    </div>
  );
};

export default ProblemDetail;
